import { motion, AnimatePresence } from 'framer-motion';
import { useState, useEffect } from 'react';

function UsersList({ users, currentUserId, setIsMinimized }) {
  const [isExpanded, setIsExpanded] = useState(false);

  useEffect(() => {
    setIsMinimized(!isExpanded); // Update parent when minimized
  }, [isExpanded, setIsMinimized]);

  const listVariants = {
    collapsed: { 
      width: '64px', 
      height: '64px', 
      borderRadius: '50%', 
      padding: 0, 
      transition: { type: 'spring', stiffness: 300, damping: 25 } 
    },
    expanded: { 
      width: '100%', 
      height: 'auto', 
      borderRadius: '16px', 
      padding: '1.5rem', 
      transition: { type: 'spring', stiffness: 300, damping: 25 } 
    }
  };

  const contentVariants = {
    hidden: { opacity: 0, y: -20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.3, ease: 'easeInOut' } }
  };

  return (
    <motion.div
      initial="collapsed"
      animate={isExpanded ? 'expanded' : 'collapsed'}
      variants={listVariants}
      className="bg-white shadow-lg flex flex-col items-center overflow-hidden"
      style={{ maxWidth: '300px' }}
    >
      <motion.div
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        onClick={() => setIsExpanded(!isExpanded)}
        className="cursor-pointer w-16 h-16 rounded-full overflow-hidden bg-gray-200 flex items-center justify-center"
      >
        <span className="text-2xl">ðŸ‘¥</span>
      </motion.div>

      <AnimatePresence>
        {isExpanded && (
          <motion.div
            initial="hidden"
            animate="visible"
            exit="hidden"
            variants={contentVariants}
            className="w-full mt-4"
          >
            <h3 className="text-2xl font-bold text-blue-700 mb-4 text-center">Compare with Others</h3>
            <ul className="space-y-2">
              {users.map(user => (
                <li 
                  key={user._id} 
                  className={`p-2 rounded-lg flex items-center gap-2 ${user._id === currentUserId ? 'bg-blue-100 text-blue-700 font-semibold' : 'bg-gray-100'}`}
                >
                  <img
                    src={user.profile.profilePic || 'https://placehold.co/32x32?text=No+Image'}
                    alt={user.name}
                    className="w-8 h-8 rounded-full object-cover"
                    onError={e => e.target.src = 'https://placehold.co/32x32?text=No+Image'}
                  />
                  <div>
                    <span>{user.name}</span>
                    <span className="block text-sm text-gray-600">
                      Weight: {user.profile.weight} kg, Target: {user.profile.targetCalories} cal
                    </span>
                  </div>
                </li>
              ))}
            </ul>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
}

export default UsersList;
