import { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';
import ProfileSidebar from '../components/ProfileSidebar';
import WorkoutForm from '../components/WorkoutForm';
import Recommendation from '../components/Recommendation';
import Progress from '../components/Progress';
import WorkoutList from '../components/WorkoutList';
import ExerciseLibrary from '../components/ExerciseLibrary';
import UsersList from '../components/UsersList';

function DashboardPage({ token, setToken, message, setMessage }) {
  const [exercise, setExercise] = useState('');
  const [reps, setReps] = useState('');
  const [sets, setSets] = useState('');
  const [weight, setWeight] = useState('');
  const [workouts, setWorkouts] = useState([]);
  const [recommendation, setRecommendation] = useState(null);
  const [progress, setProgress] = useState(null);
  const [library, setLibrary] = useState([]);
  const [profile, setProfile] = useState({ weight: '', targetCalories: '', profilePic: '' });
  const [filter, setFilter] = useState({ startDate: '', endDate: '', exercise: '' });
  const [showProfilePrompt, setShowProfilePrompt] = useState(false);
  const [users, setUsers] = useState([]);
  const [currentUserId, setCurrentUserId] = useState(null);
  const [streak, setStreak] = useState(0);
  const [showWidgets, setShowWidgets] = useState({
    workoutForm: true,
    recommendation: true,
    progress: true,
    workoutList: true,
    library: true,
  });
  const [isProfileMinimized, setIsProfileMinimized] = useState(true);
  const [isUsersMinimized, setIsUsersMinimized] = useState(true);

  const handleWorkoutSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/workouts', { exercise, reps, sets, weight }, {
        headers: { Authorization: token }
      });
      setExercise('');
      setReps('');
      setSets('');
      setWeight('');
      fetchWorkouts();
      setStreak(streak + 1); // Increment streak
    } catch (error) {
      setMessage('Failed to save workout');
    }
  };

  const fetchWorkouts = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:5000/workouts', { 
        headers: { Authorization: token },
        params: filter
      });
      setWorkouts(response.data);
    } catch (error) {
      setMessage('Failed to fetch workouts');
    }
  }, [token, filter, setMessage]);

  const fetchRecommendation = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:5000/recommend', { headers: { Authorization: token } });
      setRecommendation(response.data);
    } catch (error) {
      setMessage('Failed to fetch recommendation');
    }
  }, [token, setMessage]);

  const fetchProgress = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:5000/progress', { headers: { Authorization: token } });
      setProgress(response.data);
    } catch (error) {
      setMessage('Failed to fetch progress');
    }
  }, [token, setMessage]);

  const fetchLibrary = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:5000/library', { headers: { Authorization: token } });
      setLibrary(response.data);
    } catch (error) {
      setMessage('Failed to fetch library');
    }
  }, [token, setMessage]);

  const fetchProfile = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:5000/profile', { headers: { Authorization: token } });
      console.log('Profile data from backend:', response.data);
      const { weight, targetCalories, profilePic, _id } = response.data;
      setProfile({
        weight: weight || '',
        targetCalories: targetCalories || '',
        profilePic: profilePic || ''
      });
      setShowProfilePrompt(!weight || !targetCalories);
      setCurrentUserId(_id);
    } catch (error) {
      setMessage('Failed to fetch profile');
      setShowProfilePrompt(true);
    }
  }, [token, setMessage]);

  const fetchUsers = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:5000/users', { headers: { Authorization: token } });
      setUsers(response.data);
    } catch (error) {
      setMessage('Failed to fetch users');
    }
  }, [token, setMessage]);

  useEffect(() => {
    if (token) {
      fetchWorkouts();
      fetchRecommendation();
      fetchProgress();
      fetchLibrary();
      fetchProfile();
      fetchUsers();

      const interval = setInterval(() => {
        fetchUsers();
      }, 10000);

      return () => clearInterval(interval);
    }
  }, [token, fetchWorkouts, fetchRecommendation, fetchProgress, fetchLibrary, fetchProfile, fetchUsers]);

  const pageVariants = {
    initial: { opacity: 0, y: 50 },
    animate: { opacity: 1, y: 0, transition: { duration: 0.5, ease: 'easeInOut' } },
    exit: { opacity: 0, y: -50, transition: { duration: 0.5, ease: 'easeInOut' } }
  };

  const buttonVariants = {
    hover: { scale: 1.05, boxShadow: '0px 4px 15px rgba(0, 0, 0, 0.2)' },
    tap: { scale: 0.95 }
  };

  return (
    <motion.div 
      variants={pageVariants} 
      initial="initial" 
      animate="animate" 
      exit="exit" 
      className="w-full min-h-screen bg-gradient-to-br from-blue-200 via-purple-200 to-pink-200 flex flex-col md:flex-row p-6 relative"
    >
      {/* Left Sidebar */}
      <div className="w-full md:w-1/4 flex flex-col gap-6">
        <ProfileSidebar 
          profile={profile} 
          setProfile={setProfile} 
          token={token} 
          showProfilePrompt={showProfilePrompt} 
          setShowProfilePrompt={setShowProfilePrompt} 
          setMessage={setMessage} 
          setIsMinimized={setIsProfileMinimized}
        />
        <UsersList 
          users={users} 
          currentUserId={currentUserId} 
          setIsMinimized={setIsUsersMinimized}
        />
      </div>

      {/* Main Dashboard */}
      <div className={`flex-1 flex flex-col ${isProfileMinimized && isUsersMinimized ? 'max-w-4xl mx-auto' : 'md:w-3/4 ml-6'}`}>
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-4xl font-extrabold text-center text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600">
            Your Fitness Dashboard
          </h1>
          <motion.button
            variants={buttonVariants}
            whileHover="hover"
            whileTap="tap"
            onClick={() => alert('Contact 123@gmail.com for Premium Subscription!\n\nFree: Basic Features\nPremium: Advanced Analytics, Expert Guidance, Custom Plans, Ad-Free, E-commerce Access')}
            className="bg-gradient-to-r from-purple-500 to-pink-500 text-white px-4 py-2 rounded-lg shadow-lg"
          >
            Premium
          </motion.button>
        </div>

        {/* Streaks & Rewards */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }} 
          animate={{ opacity: 1, y: 0 }} 
          className="bg-white p-4 rounded-lg shadow-lg mb-6"
        >
          <h2 className="text-xl font-bold text-blue-700">Streaks & Rewards</h2>
          <p>Current Streak: {streak} days</p>
          {streak >= 5 && <p className="text-green-600">🎉 Reward: 5-Day Streak Bonus!</p>}
        </motion.div>

        {/* Workout Playlist */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }} 
          animate={{ opacity: 1, y: 0 }} 
          className="bg-white p-4 rounded-lg shadow-lg mb-6"
        >
          <h2 className="text-xl font-bold text-blue-700">Workout Playlist</h2>
          <iframe 
            src="https://open.spotify.com/embed/playlist/37i9dQZF1DX70Z6B1xZ3b1" 
            width="100%" 
            height="80" 
            frameBorder="0" 
            allow="encrypted-media"
            title="Spotify Workout Playlist"
          />
        </motion.div>

        {/* Custom Widgets */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }} 
          animate={{ opacity: 1, y: 0 }} 
          className="bg-white p-4 rounded-lg shadow-lg mb-6 flex flex-wrap gap-4"
        >
          <h2 className="text-xl font-bold text-blue-700 w-full">Customize Dashboard</h2>
          {Object.keys(showWidgets).map(widget => (
            <label key={widget} className="flex items-center gap-2">
              <input
                type="checkbox"
                checked={showWidgets[widget]}
                onChange={() => setShowWidgets(prev => ({ ...prev, [widget]: !prev[widget] }))}
                className="form-checkbox h-5 w-5 text-blue-600"
              />
              <span>{widget.charAt(0).toUpperCase() + widget.slice(1).replace(/([A-Z])/g, ' $1')}</span>
            </label>
          ))}
        </motion.div>

        {showWidgets.workoutForm && (
          <WorkoutForm 
            exercise={exercise} 
            setExercise={setExercise} 
            reps={reps} 
            setReps={setReps} 
            sets={sets} 
            setSets={setSets} 
            weight={weight} 
            setWeight={setWeight} 
            handleWorkoutSubmit={handleWorkoutSubmit} 
          />
        )}
        {showWidgets.recommendation && <Recommendation recommendation={recommendation} />}
        {showWidgets.progress && <Progress progress={progress} workouts={workouts} />}
        {showWidgets.workoutList && (
          <WorkoutList 
            workouts={workouts} 
            filter={filter} 
            setFilter={setFilter} 
            fetchWorkouts={fetchWorkouts} 
            token={token} 
          />
        )}
        {showWidgets.library && <ExerciseLibrary library={library} />}
        <motion.button 
          variants={buttonVariants}
          whileHover="hover"
          whileTap="tap"
          onClick={() => setToken(null)} 
          className="bg-red-600 text-white p-3 rounded-lg shadow-lg w-full max-w-xs mx-auto"
        >
          Logout
        </motion.button>
        {message && (
          <motion.p 
            initial={{ opacity: 0 }} 
            animate={{ opacity: 1 }} 
            className="text-center mt-4 text-red-500 font-semibold"
          >
            {message}
          </motion.p>
        )}
      </div>
    </motion.div>
  );
}

export default DashboardPage;
