import { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import { motion, AnimatePresence } from 'framer-motion';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function App() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [isLogin, setIsLogin] = useState(false);
  const [token, setToken] = useState(null);
  const [exercise, setExercise] = useState('');
  const [reps, setReps] = useState('');
  const [sets, setSets] = useState('');
  const [weight, setWeight] = useState('');
  const [workouts, setWorkouts] = useState([]);
  const [recommendation, setRecommendation] = useState(null);
  const [progress, setProgress] = useState(null);
  const [library, setLibrary] = useState([]);
  const [profile, setProfile] = useState({ weight: '', targetCalories: '' });
  const [filter, setFilter] = useState({ startDate: '', endDate: '', exercise: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const url = isLogin ? 'http://localhost:5000/login' : 'http://localhost:5000/signup';
    try {
      const response = await axios.post(url, { name, email, password, ...profile });
      if (isLogin) {
        setMessage('Login successful!');
        setToken(response.data.token);
      } else {
        setMessage('Account created! Please log in.');
        setName('');
        setEmail('');
        setPassword('');
        setProfile({ weight: '', targetCalories: '' });
        setIsLogin(true);
      }
    } catch (error) {
      setMessage(error.response?.data || 'Something went wrong!');
    }
  };

  const handleWorkoutSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/workouts', { exercise, reps, sets, weight }, {
        headers: { Authorization: token }
      });
      setExercise('');
      setReps('');
      setSets('');
      setWeight('');
      fetchWorkouts();
    } catch (error) {
      setMessage('Failed to save workout');
    }
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put('http://localhost:5000/profile', profile, {
        headers: { Authorization: token }
      });
      setMessage('Profile updated!');
      fetchRecommendation();
    } catch (error) {
      setMessage('Failed to update profile');
    }
  };

  const fetchWorkouts = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:5000/workouts', { 
        headers: { Authorization: token },
        params: filter
      });
      setWorkouts(response.data);
    } catch (error) {
      setMessage('Failed to fetch workouts');
    }
  }, [token, filter]);

  const fetchRecommendation = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:5000/recommend', { headers: { Authorization: token } });
      setRecommendation(response.data);
    } catch (error) {
      setMessage('Failed to fetch recommendation');
    }
  }, [token]);

  const fetchProgress = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:5000/progress', { headers: { Authorization: token } });
      setProgress(response.data);
    } catch (error) {
      setMessage('Failed to fetch progress');
    }
  }, [token]);

  const fetchLibrary = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:5000/library');
      setLibrary(response.data);
    } catch (error) {
      setMessage('Failed to fetch library');
    }
  }, []);

  const fetchProfile = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:5000/profile', { headers: { Authorization: token } });
      setProfile(response.data);
    } catch (error) {
      setMessage('Failed to fetch profile');
    }
  }, [token]);

  useEffect(() => {
    if (token) {
      fetchWorkouts();
      fetchRecommendation();
      fetchProgress();
      fetchLibrary();
      fetchProfile();
    }
  }, [token, fetchWorkouts, fetchRecommendation, fetchProgress, fetchLibrary, fetchProfile]);

  const pageVariants = {
    initial: { opacity: 0, y: 50 },
    animate: { opacity: 1, y: 0, transition: { duration: 0.5 } },
    exit: { opacity: 0, y: -50, transition: { duration: 0.5 } }
  };

  const chartData = {
    labels: workouts.map(w => new Date(w.date).toLocaleDateString()),
    datasets: [{
      label: 'Calories Burned',
      data: workouts.map(w => w.calories),
      borderColor: '#10b981',
      backgroundColor: 'rgba(16, 185, 129, 0.2)',
      fill: true
    }]
  };

  return (
    <AnimatePresence mode="wait">
      {token ? (
        <motion.div 
          key="dashboard" 
          variants={pageVariants} 
          initial="initial" 
          animate="animate" 
          exit="exit" 
          className="max-w-4xl mx-auto p-6 bg-gradient-to-br from-blue-200 via-purple-200 to-pink-200 min-h-screen"
        >
          <h1 className="text-4xl font-extrabold text-center mb-8 text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600">
            Your Fitness Dashboard
          </h1>

          {/* Profile */}
          <motion.div 
            initial={{ scale: 0.9 }} 
            animate={{ scale: 1 }} 
            transition={{ duration: 0.3 }} 
            className="bg-white p-6 rounded-xl shadow-lg mb-6 border-l-4 border-blue-500"
          >
            <h3 className="text-2xl font-bold mb-4 text-blue-700">Your Profile</h3>
            <form onSubmit={handleProfileSubmit} className="flex flex-col gap-4">
              <input 
                type="number" 
                placeholder="Weight (kg)" 
                value={profile.weight} 
                onChange={(e) => setProfile({ ...profile, weight: e.target.value })} 
                className="p-3 border rounded-lg focus:ring-2 focus:ring-blue-400 focus:outline-none bg-blue-50"
              />
              <input 
                type="number" 
                placeholder="Target Calories" 
                value={profile.targetCalories} 
                onChange={(e) => setProfile({ ...profile, targetCalories: e.target.value })} 
                className="p-3 border rounded-lg focus:ring-2 focus:ring-blue-400 focus:outline-none bg-blue-50"
              />
              <motion.button 
                whileHover={{ scale: 1.05 }} 
                whileTap={{ scale: 0.95 }} 
                type="submit" 
                className="bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 transition-colors"
              >
                Update Profile
              </motion.button>
            </form>
          </motion.div>

          {/* Workout Form */}
          <motion.div 
            initial={{ scale: 0.9 }} 
            animate={{ scale: 1 }} 
            transition={{ duration: 0.3 }} 
            className="bg-white p-6 rounded-xl shadow-lg mb-6 border-l-4 border-green-500"
          >
            <h3 className="text-2xl font-bold mb-4 text-green-700">Log a Workout</h3>
            <form onSubmit={handleWorkoutSubmit} className="flex gap-4 flex-wrap">
              <input 
                type="text" 
                placeholder="Exercise" 
                value={exercise} 
                onChange={(e) => setExercise(e.target.value)} 
                className="p-3 border rounded-lg focus:ring-2 focus:ring-green-400 focus:outline-none bg-green-50"
              />
              <input 
                type="number" 
                placeholder="Reps" 
                value={reps} 
                onChange={(e) => setReps(e.target.value)} 
                className="p-3 border rounded-lg focus:ring-2 focus:ring-green-400 focus:outline-none bg-green-50"
              />
              <input 
                type="number" 
                placeholder="Sets" 
                value={sets} 
                onChange={(e) => setSets(e.target.value)} 
                className="p-3 border rounded-lg focus:ring-2 focus:ring-green-400 focus:outline-none bg-green-50"
              />
              <input 
                type="number" 
                placeholder="Weight (kg)" 
                value={weight} 
                onChange={(e) => setWeight(e.target.value)} 
                className="p-3 border rounded-lg focus:ring-2 focus:ring-green-400 focus:outline-none bg-green-50"
              />
              <motion.button 
                whileHover={{ scale: 1.05 }} 
                whileTap={{ scale: 0.95 }} 
                type="submit" 
                className="bg-green-600 text-white p-3 rounded-lg hover:bg-green-700 transition-colors"
              >
                Log
              </motion.button>
            </form>
          </motion.div>

          {/* Recommendation */}
          {recommendation && (
            <motion.div 
              initial={{ opacity: 0, y: 20 }} 
              animate={{ opacity: 1, y: 0 }} 
              transition={{ duration: 0.5 }} 
              className="bg-white p-6 rounded-xl shadow-lg mb-6 border-l-4 border-purple-500"
            >
              <h3 className="text-2xl font-bold mb-4 text-purple-700">Recommended Workout</h3>
              <p className="text-gray-700 text-lg">{recommendation.exercise} - {recommendation.reps} reps, {recommendation.sets} sets, {recommendation.weight}kg</p>
            </motion.div>
          )}

          {/* Progress */}
          {progress && (
            <motion.div 
              initial={{ opacity: 0, y: 20 }} 
              animate={{ opacity: 1, y: 0 }} 
              transition={{ duration: 0.5, delay: 0.2 }} 
              className="bg-white p-6 rounded-xl shadow-lg mb-6 border-l-4 border-yellow-500"
            >
              <h3 className="text-2xl font-bold mb-4 text-yellow-700">Progress</h3>
              <p className="text-gray-700">Total Workouts: <span className="font-semibold">{progress.totalWorkouts}</span></p>
              <p className="text-gray-700">Total Calories: <span className="font-semibold">{progress.totalCalories}</span></p>
              <p className="text-gray-700">Avg Calories/Workout: <span className="font-semibold">{progress.avgCalories}</span></p>
              <Line data={chartData} options={{ responsive: true, plugins: { legend: { position: 'top' } } }} />
            </motion.div>
          )}

          {/* Workouts with Filters */}
          <motion.div 
            initial={{ opacity: 0, y: 20 }} 
            animate={{ opacity: 1, y: 0 }} 
            transition={{ duration: 0.5, delay: 0.4 }} 
            className="bg-white p-6 rounded-xl shadow-lg mb-6 border-l-4 border-pink-500"
          >
            <h3 className="text-2xl font-bold mb-4 text-pink-700">Your Workouts</h3>
            <div className="flex gap-4 flex-wrap mb-4">
              <input 
                type="date" 
                value={filter.startDate} 
                onChange={(e) => setFilter({ ...filter, startDate: e.target.value })} 
                className="p-2 border rounded-lg focus:ring-2 focus:ring-pink-400 focus:outline-none bg-pink-50"
              />
              <input 
                type="date" 
                value={filter.endDate} 
                onChange={(e) => setFilter({ ...filter, endDate: e.target.value })} 
                className="p-2 border rounded-lg focus:ring-2 focus:ring-pink-400 focus:outline-none bg-pink-50"
              />
              <input 
                type="text" 
                placeholder="Filter by exercise" 
                value={filter.exercise} 
                onChange={(e) => setFilter({ ...filter, exercise: e.target.value })} 
                className="p-2 border rounded-lg focus:ring-2 focus:ring-pink-400 focus:outline-none bg-pink-50"
              />
              <motion.button 
                whileHover={{ scale: 1.05 }} 
                whileTap={{ scale: 0.95 }} 
                onClick={fetchWorkouts} 
                className="bg-pink-600 text-white p-2 rounded-lg hover:bg-pink-700 transition-colors"
              >
                Apply Filter
              </motion.button>
            </div>
            <ul className="space-y-2">
              {workouts.map(w => (
                <motion.li 
                  key={w._id} 
                  initial={{ opacity: 0, x: -20 }} 
                  animate={{ opacity: 1, x: 0 }} 
                  transition={{ duration: 0.3 }} 
                  className="text-gray-700 hover:bg-pink-50 p-2 rounded"
                >
                  {w.exercise} - {w.reps} reps, {w.sets} sets, {w.calories} cal ({new Date(w.date).toLocaleDateString()})
                </motion.li>
              ))}
            </ul>
          </motion.div>

          {/* Library */}
          <motion.div 
            initial={{ opacity: 0, y: 20 }} 
            animate={{ opacity: 1, y: 0 }} 
            transition={{ duration: 0.5, delay: 0.6 }} 
            className="bg-white p-6 rounded-xl shadow-lg mb-6 border-l-4 border-teal-500"
          >
            <h3 className="text-2xl font-bold mb-4 text-teal-700">Exercise Library</h3>
            <ul className="space-y-2">
              {library.map((e, i) => (
                <motion.li 
                  key={i} 
                  initial={{ opacity: 0, x: -20 }} 
                  animate={{ opacity: 1, x: 0 }} 
                  transition={{ duration: 0.3 }} 
                  className="text-gray-700 hover:bg-teal-50 p-2 rounded"
                >
                  <a href={e.demo} target="_blank" rel="noopener noreferrer" className="text-teal-500 hover:underline font-semibold">{e.name}</a> - {e.demo}
                </motion.li>
              ))}
            </ul>
          </motion.div>

          <motion.button 
            whileHover={{ scale: 1.05 }} 
            whileTap={{ scale: 0.95 }} 
            onClick={() => setToken(null)} 
            className="bg-red-600 text-white p-3 rounded-lg hover:bg-red-700 transition-colors w-full"
          >
            Logout
          </motion.button>
          {message && (
            <motion.p 
              initial={{ opacity: 0 }} 
              animate={{ opacity: 1 }} 
              className="text-center mt-4 text-red-500 font-semibold"
            >
              {message}
            </motion.p>
          )}
        </motion.div>
      ) : (
        <motion.div 
          key="auth" 
          variants={pageVariants} 
          initial="initial" 
          animate="animate" 
          exit="exit" 
          className="max-w-md mx-auto p-6 bg-gradient-to-br from-blue-200 via-purple-200 to-pink-200 min-h-screen flex flex-col justify-center"
        >
          <h1 className="text-4xl font-extrabold text-center mb-8 text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600">
            Fitness Tracker
          </h1>
          <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">{isLogin ? 'Login' : 'Sign Up'}</h2>
          <form onSubmit={handleSubmit} className="flex flex-col gap-4">
            {!isLogin && (
              <>
                <motion.input 
                  initial={{ opacity: 0, y: 20 }} 
                  animate={{ opacity: 1, y: 0 }} 
                  transition={{ duration: 0.3 }} 
                  type="text" 
                  placeholder="Name" 
                  value={name} 
                  onChange={(e) => setName(e.target.value)} 
                  className="p-3 border rounded-lg focus:ring-2 focus:ring-purple-400 focus:outline-none bg-purple-50"
                />
                <motion.input 
                  initial={{ opacity: 0, y: 20 }} 
                  animate={{ opacity: 1, y: 0 }} 
                  transition={{ duration: 0.3, delay: 0.1 }} 
                  type="number" 
                  placeholder="Weight (kg)" 
                  value={profile.weight} 
                  onChange={(e) => setProfile({ ...profile, weight: e.target.value })} 
                  className="p-3 border rounded-lg focus:ring-2 focus:ring-purple-400 focus:outline-none bg-purple-50"
                />
                <motion.input 
                  initial={{ opacity: 0, y: 20 }} 
                  animate={{ opacity: 1, y: 0 }} 
                  transition={{ duration: 0.3, delay: 0.2 }} 
                  type="number" 
                  placeholder="Target Calories" 
                  value={profile.targetCalories} 
                  onChange={(e) => setProfile({ ...profile, targetCalories: e.target.value })} 
                  className="p-3 border rounded-lg focus:ring-2 focus:ring-purple-400 focus:outline-none bg-purple-50"
                />
              </>
            )}
            <motion.input 
              initial={{ opacity: 0, y: 20 }} 
              animate={{ opacity: 1, y: 0 }} 
              transition={{ duration: 0.3, delay: isLogin ? 0 : 0.3 }} 
              type="email" 
              placeholder="Email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              className="p-3 border rounded-lg focus:ring-2 focus:ring-purple-400 focus:outline-none bg-purple-50"
            />
            <motion.input 
              initial={{ opacity: 0, y: 20 }} 
              animate={{ opacity: 1, y: 0 }} 
              transition={{ duration: 0.3, delay: isLogin ? 0.1 : 0.4 }} 
              type="password" 
              placeholder="Password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              className="p-3 border rounded-lg focus:ring-2 focus:ring-purple-400 focus:outline-none bg-purple-50"
            />
            <motion.button 
              whileHover={{ scale: 1.05 }} 
              whileTap={{ scale: 0.95 }} 
              type="submit" 
              className="bg-purple-600 text-white p-3 rounded-lg hover:bg-purple-700 transition-colors"
            >
              {isLogin ? 'Login' : 'Sign Up'}
            </motion.button>
          </form>
          <motion.button 
            whileHover={{ scale: 1.05 }} 
            whileTap={{ scale: 0.95 }} 
            onClick={() => setIsLogin(!isLogin)} 
            className="bg-gray-500 text-white p-3 rounded-lg hover:bg-gray-600 transition-colors mt-4"
          >
            {isLogin ? 'Need to sign up?' : 'Already have an account? Login'}
          </motion.button>
          {message && (
            <motion.p 
              initial={{ opacity: 0 }} 
              animate={{ opacity: 1 }} 
              className="text-center mt-4 text-red-500 font-semibold"
            >
              {message}
            </motion.p>
          )}
        </motion.div>
      )}
    </AnimatePresence>
  );
}

export default App;
