import { motion } from 'framer-motion';
import axios from 'axios';

function ProfileSidebar({ profile, setProfile, token, showProfilePrompt, setShowProfilePrompt, setMessage }) {
  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    try {
      // Ensure profilePic defaults to /9au02y.jpg if empty
      const updatedProfile = { ...profile, profilePic: profile.profilePic || 'client\src/9au02y.jpg' };
      await axios.put('http://localhost:5000/profile', updatedProfile, {
        headers: { Authorization: token }
      });
      setProfile(updatedProfile); // Update local state
      setMessage('Profile updated!');
      setShowProfilePrompt(false);
    } catch (error) {
      setMessage('Failed to update profile');
    }
  };

  return (
    <motion.div 
      initial={{ opacity: 0, x: -50 }} 
      animate={{ opacity: 1, x: 0 }} 
      transition={{ duration: 0.5 }} 
      className="w-full md:w-1/4 bg-white p-6 rounded-xl shadow-lg mb-6 md:mb-0 md:mr-6 flex flex-col items-center"
    >
      <h3 className="text-2xl font-bold text-blue-700 mb-4">Your Profile</h3>
      {profile.profilePic && (
        <img 
          src={profile.profilePic} 
          alt="Profile" 
          className="w-16 h-16 rounded-full mb-4"
          onError={() => console.log('Image failed to load:', profile.profilePic)} // Debug if image fails
        />
      )}
      {showProfilePrompt ? (
        <form onSubmit={handleProfileSubmit} className="flex flex-col gap-4 w-full">
          <input 
            type="number" 
            placeholder="Weight (kg)" 
            value={profile.weight} 
            onChange={(e) => setProfile({ ...profile, weight: e.target.value })} 
            className="p-3 border rounded-lg focus:ring-2 focus:ring-blue-400 focus:outline-none bg-blue-50"
          />
          <input 
            type="number" 
            placeholder="Target Calories" 
            value={profile.targetCalories} 
            onChange={(e) => setProfile({ ...profile, targetCalories: e.target.value })} 
            className="p-3 border rounded-lg focus:ring-2 focus:ring-blue-400 focus:outline-none bg-blue-50"
          />
          <input 
            type="text" 
            placeholder="Profile Picture URL" 
            value={profile.profilePic} 
            onChange={(e) => setProfile({ ...profile, profilePic: e.target.value })} 
            className="p-3 border rounded-lg focus:ring-2 focus:ring-blue-400 focus:outline-none bg-blue-50"
          />
          <motion.button 
            whileHover={{ scale: 1.05 }} 
            whileTap={{ scale: 0.95 }} 
            type="submit" 
            className="bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 transition-colors"
          >
            Save Profile
          </motion.button>
        </form>
      ) : (
        <div className="text-gray-700 text-center">
          <p><span className="font-semibold">Weight:</span> {profile.weight} kg</p>
          <p><span className="font-semibold">Target Calories:</span> {profile.targetCalories}</p>
        </div>
      )}
    </motion.div>
  );
}

export default ProfileSidebar;
