import { motion } from 'framer-motion';

function UsersList({ users, currentUserId }) {
  return (
    <motion.div 
      initial={{ opacity: 0, y: 20 }} 
      animate={{ opacity: 1, y: 0 }} 
      transition={{ duration: 0.5, delay: 0.8 }} 
      className="bg-white p-6 rounded-xl shadow-lg border-l-4 border-indigo-500"
    >
      <h3 className="text-2xl font-bold mb-4 text-indigo-700">Compare with Others</h3>
      <ul className="space-y-4">
        {users.filter(u => u._id !== currentUserId).map(u => (
          <motion.li 
            key={u._id} 
            initial={{ opacity: 0, x: -20 }} 
            animate={{ opacity: 1, x: 0 }} 
            transition={{ duration: 0.3 }} 
            className="flex items-center space-x-4"
          >
            <img 
              src={u.profile.profilePic || 'https://avatar.example.com/default2.png'} 
              alt={u.name} 
              className="w-12 h-12 rounded-full"
            />
            <div>
              <p className="font-semibold text-gray-700">{u.name}</p>
              <p className="text-gray-600">Weight: {u.profile.weight} kg</p>
              <p className="text-gray-600">Target Calories: {u.profile.targetCalories}</p>
            </div>
          </motion.li>
        ))}
      </ul>
    </motion.div>
  );
}

export default UsersList;
